<template>
  <a-layout class="w-login" :class="contentClass">
    <a-layout-header class="w-login-header">
      <img :src="logo" class="w-login-logo" />
    </a-layout-header>
    <a-layout-content class="w-login-content">
      <a-form
        class="w-login-form"
        :wrapper-col="wrapperCol"
        :model="formState"
        @finish="onFinish"
      >
        <a-divider>WTM VUE3</a-divider>
        <a-form-item>
          <a-input v-model:value="formState.account" placeholder="Username">
            <template #prefix>
              <UserOutlined style="color: rgba(0, 0, 0, 0.25)" />
            </template>
          </a-input>
        </a-form-item>
        <a-form-item>
          <a-input
            v-model:value="formState.password"
            type="password"
            placeholder="Password"
          >
            <template #prefix>
              <LockOutlined style="color: rgba(0, 0, 0, 0.25)" />
            </template>
          </a-input>
        </a-form-item>
        <a-form-item>
          <a-button
            type="primary"
            html-type="submit"
            :loading="System.UserController.loading"
            :disabled="disabled"
          >
            <i18n-t :keypath="$locales.action_login" />
          </a-button>
        </a-form-item>
      </a-form>
    </a-layout-content>
    <a-layout-footer>
      <a-descriptions>
        <a-descriptions-item v-for="item in links">
          <a
            slot="label"
            :href="item.url"
            target="_blank"
            v-text="item.name"
          ></a>
        </a-descriptions-item>
      </a-descriptions>
    </a-layout-footer>
  </a-layout>
</template>
<script lang="ts">
import { SystemController } from "@/client";
import { Inject, Options, Vue } from "vue-property-decorator";
@Options({ components: {} })
export default class extends Vue {
  /**
   * 从 Aapp 中 注入 系统管理
   */
  @Inject({ from: SystemController.Symbol }) System: SystemController;
  formState = {
    account: "admin",
    password: "000000"
  };
  wrapperCol = { span: 24 };
  get logo() {
    return require("@/assets/img/logo.png");
  }
  links = [
    { name: "GitHub", url: "https://github.com/dotnetcore/WTM" },
    { name: "Vue", url: "https://vue3js.cn/docs/zh/" },
    { name: "Ant Design", url: "https://2x.antdv.com/components/overview/" },
    { name: "Rxjs", url: "https://rxjs.dev/" },
    { name: "Mobx", url: "https://mobx.js.org/" },
    { name: "Lodash", url: " https://lodash.com/" }
  ];
  async onFinish(values) {
    try {
      await this.System.UserController.onSignIn(this.formState);
    } catch (error) {
      this.$message.error(
        this.$t(this.$locales.tips_request_error, {
          label: this.$t(this.$locales.action_login)
        })
      );
    }
  }
  get disabled() {
    return this.formState.account === "" || this.formState.password === "";
  }
  get contentClass() {
    return "w-login-back-" + this.lodash.sample(this.lodash.range(1, 5));
  }
  mounted() {
  }
  created() {}
}
</script>
